import { Component } from '@angular/core';
import { ChildrenOutletContexts } from '@angular/router';
import { slideInAnimation } from '../../route-animations';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss']
})
export class AppComponent {
  title = 'portfolio-client';
  
  constructor(private contexts: ChildrenOutletContexts) {}
  getRouteAnimationData() {
    return this.contexts.getContext('primary')?.route?.snapshot?.data?.['animation'];
  }
}
<div id="container" class="app-container">
  <header>
    <nav>
      <div id="social" class="right">
                <a href="https://www.facebook.com/piotr.markiewicz.96" id="fb-link" target="_blank" aria-label="Odwiedź mnie na Facebooku"><i class="fab fa-facebook-square"></i></a>
                <a href="tel:+48798613113" id="phone-link" aria-label="Zadzwoń i zapytaj o realizację zadania"><i class="fa fa-phone" aria-hidden="true"></i></a>
                <a href="https://www.youtube.com/@PiotrMarkiewicz-s1v" id="youtube-link" target="_blank"  aria-label="Odwiedź mój kanał na YouTube"><i class="fab fa-youtube-square"></i></a>
                <a href="https://www.linkedin.com/in/piotr-markiewicz-339435131/" id="linkedin-link" target="_blank" aria-label="Odwiedź mnie na LinkedIn"><i class="fab fa-linkedin"></i></a>
                <a href="mailto:strony.pm@gmail.com" id="email-link" aria-label="Wyślij e-mail do Anety"><i class="fas fa-envelope"></i></a>
            </div>
      <!-- <ul>
        <li><a routerLink="/projects" routerLinkActive="active">Projekty</a></li>
      </ul> -->
    </nav>
  </header>

  <div class="app-container">
    <app-navbar></app-navbar>
    <main>
      <router-outlet></router-outlet>
    </main>
    <app-footer></app-footer>
  </div>
</div>
