import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { map, Observable } from 'rxjs';

export interface ContactForm {
  name: string;
  email: string;
  message: string;
}

@Injectable({
  providedIn: 'root'
})
export class ContactService {

  constructor(private http: HttpClient) { }

  postContactForm(form: ContactForm): Observable<any> {
    return this.http.post("http://localhost:4200/api/contact", form)
  }

  getCourses(): Observable<string[]> {
          return this.http.get<{ courses: string[] }>('http://localhost:4200/api/courses')
          .pipe(map(res => res.courses));
      }
}
